import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { doctorController } from "../../controllers/doctor";
import Layout from "./layout";
import "../../css/schedule-settings.css";

const ScheduleSettings = () => {
  const { id } = useParams();
  const [loading, setLoading] = useState(true);
  const [doctorData, setDoctorData] = useState({
    id: -1,
    name: "",
    country: "",
    profession_fk: -1,
    city: "",
    Price: "",
    location: [],
  });
  useEffect(() => {
    doctorController
      .getDoctorInfo(id)
      .then((response) => {
        setDoctorData(response.data);
      })
      .then(() => {
        setLoading(false);
        console.log(doctorData);
      });
  }, []);

  const [timingArray, setTimingarray] = useState([
    {
      i: 0,
      start: "",
      end: "",
    },
  ]);
  const addTiming = () => {
    let temp = timingArray;
    temp = [...temp, { i: timingArray.length + 1, start: "", end: "" }];
    setTimingarray(temp);
  };
  const deleteTiming = (i) => {
    console.log(i);
    let temp = timingArray;
    console.log(temp);

    temp = temp.filter((timing) => timing.i !== i);
    setTimingarray(temp);
  };

  return (
    <Layout>
      {!loading && (
        <div className="d-flex justify-content-center">
          <div className="col-8 schedule-div">
            <h3>Weekly Schedule</h3>
            <br />
            <div className="d-flex flex-column">
              <span>Location</span>
              <select>
                <option selected disabled>
                  Select our location
                </option>
                {doctorData.location.map((l) => {
                  return <option>{l}</option>;
                })}
              </select>
            </div>
            <br />
            <div className="d-flex flex-column">
              <span>Add your timings</span>
              <input
                type="button"
                className="input"
                value="Add Timing"
                onClick={() => addTiming()}
              />
              <div className="d-flex w-100 flex-column justify-content-center">
                {timingArray.map((time, i) => {
                  return (
                    <div className="d-flex  justify-content-center">
                      <input
                        className="input mx-4"
                        placeholder="Start Time"
                        type="time"
                      />
                      <input
                        className="input"
                        placeholder="End Time"
                        type="time"
                      />
                      <input
                        type="button"
                        value={"X"}
                        className="mx-4 input close"
                        onClick={() => deleteTiming(i)}
                      />
                    </div>
                  );
                })}
              </div>
            </div>
          </div>
        </div>
      )}
    </Layout>
  );
};

export default ScheduleSettings;
