import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { doctorController } from "../../controllers/doctor";
import Layout from "./layout";
import "../../css/schedule-settings.css";

const ScheduleSettings = () => {
  const { id } = useParams();
  const [loading, setLoading] = useState(true);
  const [doctorData, setDoctorData] = useState({
    id: -1,
    name: "",
    country: "",
    profession_fk: -1,
    city: "",
    Price: "",
    location: [],
  });
  useEffect(() => {
    doctorController
      .getDoctorInfo(id)
      .then((response) => {
        setDoctorData(response.data);
      })
      .then(() => {
        setLoading(false);
        console.log(doctorData);
      });
  }, []);

  const [times, setTimes] = useState([
    {
      start: "",
      end: "",
    },
  ]);
  const addTiming = () => {
    let temp = times;
    temp = [...temp, { start: "", end: "" }];
    setTimes(temp);
  };

  return (
    <Layout>
      {!loading && (
        <div className="d-flex flex-column container-fluid col-12 w-100 schedule-div">
          <select>
            <option disabled>Select our location</option>
            {doctorData.location.map((l) => {
              return <option>{l}</option>;
            })}
          </select>
          <div>
            <input
              type="button"
              className="input"
              value="Add Timing"
              onClick={() => addTiming()}
            />
            {times.map((time) => {
              return (
                <div className="d-flex justify-content-between">
                  <input
                    className="input"
                    placeholder="Start Time"
                    type="time"
                  />
                  <input className="input" placeholder="End Time" type="time" />
                </div>
              );
            })}
          </div>
        </div>
      )}
    </Layout>
  );
};

export default ScheduleSettings;
