import React, { useState, useRef } from "react";
import { Modal } from "react-bootstrap";
import SelectYears from "../inputs/select-years";
import "bootstrap/dist/css/bootstrap.min.css";
import "../css/registration-modal.css";
import { useForm, Controller } from "react-hook-form";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

const RegistrationModal = ({ ...props }) => {
  const { register, handleSubmit, reset, formState, watch, control } = useForm({
    mode: "onChange",
  });
  const { errors, isDirty, isSubmitting, isValid } = formState;
  const password = useRef();
  password.current = watch("password", "");

  const onSubmitHandler = (data) => {
    console.log({ data });
    reset();
  };

  return (
    <Modal
      {...props}
      size="lg"
      aria-labelledby="contained-modal-title-vcenter"
      centered
    >
      <Modal.Header closeButton>
        <Modal.Title id="contained-modal-title-vcenter">
          <div className="signup-title">
            <span className="title">Sign Up</span> <br />
            <span className="subtitle">It's quick and easy.</span>
          </div>
        </Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <div className="signup-form">
          <form onSubmit={handleSubmit(onSubmitHandler)}>
            <div className="common-fields">
              <div className="form-group d-flex justify-content-between">
                <select placeholder="Prefix">
                  <option selected disabled>
                    Prefix
                  </option>
                  <option>Mr.</option>
                  <option>Ms.</option>
                  <option>Mrs.</option>
                  <option>Dr.</option>
                  <option>Prof.</option>
                </select>
                <div className="flex-column d-flex">
                  <input
                    type="text"
                    placeholder="First Name"
                    className="input"
                    required
                    {...register("firstName", {
                      pattern: /^[A-Za-z]+$/i,
                    })}
                  />
                  <div className="error">
                    {errors.firstName?.type === "pattern" &&
                      "Invalid Format, special characters are not allowed"}
                  </div>
                </div>
                <div className="flex-column d-flex">
                  <input
                    type="text"
                    placeholder="Last Name"
                    className="input"
                    {...register("lastName", {
                      pattern: /^[A-Za-z]+$/i,
                    })}
                  />
                  <div className="error">
                    {errors.lastName?.type === "pattern" &&
                      "Invalid Format, special characters are not allowed"}
                  </div>
                </div>
              </div>

              <div className="form-group d-flex flex-column">
                <input
                  type="email"
                  placeholder="Email"
                  required
                  className="input full-width"
                  {...register("email", {
                    pattern: /^[\w-\.]+@([\w-]+\.)+[\w-]{2,4}$/g,
                  })}
                />
                <div className="error">
                  {errors.email?.type === "pattern" && "Invalid email format"}
                </div>
              </div>

              <div className="form-group d-flex flex-column">
                <input
                  type="password"
                  placeholder="New Password"
                  className="input full-width"
                  {...register("password", {
                    pattern: /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)[a-zA-Z\d]{8,}$/,
                  })}
                />
                <div className="error">
                  {errors.password?.type === "pattern" &&
                    "Invalid format, please retype password"}
                </div>
              </div>

              <div className="form-group d-flex flex-column">
                <input
                  type="password"
                  placeholder="Confirm Password"
                  className="input full-width"
                  ref={register("confirm_password", {
                    validate: (value) =>
                      value === password.current ||
                      "The passwords do not match",
                  })}
                />
                <div className="error">
                  {errors.password_repeat && (
                    <p>{errors.password_repeat.message}</p>
                  )}
                </div>
              </div>

              <div>
                <Controller
                  {...register("dob")}
                  control={control}
                  defaultValue={new Date()}
                  render={({ field: { onChange, value } }) => {
                    return (
                      <DatePicker
                        className="dob full-width"
                        onChange={onChange}
                        required
                        selected={value}
                        placeholderText="Enter your birth date"
                      />
                    );
                  }}
                />
              </div>

              <div className="form-group d-flex flex-column">
                <select
                  {...register("country", {
                    validate: (value) =>
                      value !== "" 
                  })}
                  required
                  className="w-100"
                >
                  <option disabled selected value="">Select your country</option>
                  <option>Lebanon</option>
                  <option>US</option>
                  <option>France</option>
                </select>
                <div className="error">
                  {errors.country && <p>Select a country</p>}
                </div>
              </div>
              <div className="form-group d-flex flex-column">
                <div className="d-flex">
                  <input type="text" className="input" disabled value="+961" />
                  <input
                    type="text"
                    placeholder="Phone Number"
                    className="input full-width"
                    {...register("phone_number", {
                      pattern: /^[0-9]*$/,
                    })}
                  />
                </div>
                <div className="error">
                  {errors.phone_number?.type === "pattern" && "Invalid format"}
                </div>
              </div>

              <div className="form-group d-flex flex-column">
                <span>Terms and conditions</span>
                <div className="d-flex align-items-center form-check w-full">
                  <input type="checkbox" />
                  <label>
                    I agree with the <a href=""> terms and condition </a>
                  </label>
                </div>
              </div>

              <div className="form-group d-flex flex-column w-full">
                <span>Newsletter subscription</span>
                <div className="d-flex align-items-center form-check w-full">
                  <input type="checkbox" />
                  <label>
                    I agree with the <a href=""> terms and condition </a>
                  </label>
                </div>
              </div>
            </div>

            <div className="d-flex align-items-center justify-content-center">
              <div className="signup-btn d-flex justify-content-center">
                <input
                  className="btn"
                  type="submit"
                  disabled={!isDirty || !isValid}
                />
              </div>
            </div>
          </form>
        </div>
      </Modal.Body>
    </Modal>
  );
};

export default RegistrationModal;
