import { useRef, useState } from "react";
import "../css/price-range.css";

const PriceRange = () => {
  const [startPos, setStartPos] = useState(0);
  const sliderRef = useRef();
  const startRef = useRef();
  const [startMouseDown, setStartMouseDown] = useState(false);
  const [startMouseUp, setStartMouseUp] = useState(false);
  const [startMouseX, setStartMouseX] = useState(0);
  console.log(startMouseDown)

  const setPositions = (e) => {
    const x = e.nativeEvent.offsetX;
    if (startMouseDown) {
      startRef.current.style.left = x + startMouseX + "px";
    }
  };
  const stopMoving = () => {
    if (startMouseDown) {
      setStartMouseDown(false);
    }
  };

  return (
    <div
      ref={sliderRef}
      onMouseMove={(e) => setPositions(e)}
      className="slider-range d-flex"
    >
      <div
        disabled
        ref={startRef}
        onMouseDown={(e) => setStartMouseDown(true)}
        onMouseUp={() => setStartMouseDown(false)}
        onMouseMove={(e) => setStartMouseX(e.nativeEvent.offsetX)}
        onMouseLeave={() => stopMoving()}
        className="start-range"
      ></div>
      <div className="end-range"></div>
    </div>
  );
};

export default PriceRange;
