import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { productsController } from "../../controllers/product-page";
import "../../css/products-main.css";
import DownChevron from "../../icons/down-chevron";
import Close from "../../icons/close";
import SearchIcon from "../../icons/search-icon";
import laptop1 from "../../laptop1.jpeg";
import { Outlet, Link } from "react-router-dom";
import soulmed from "../../logo.png" 
import {
  ReactiveBase,
  RangeSlider,
  SelectedFilters,
  ResultList,
  ReactiveList,
} from "@appbaseio/reactivesearch";
import classNames from "classnames";
import CustomPagination from "../../inputs/custom-pagination";
import NavBar from "../../components/nav-bar";
import PriceRange from "../../inputs/price-range";
import SliderAnimation from "../../inputs/slider";

const Product = ({ ...props }) => {
  let { productId } = useParams();
  const [productsList, setProductsList] = useState([]);
  const [maxPrice, setMaxPrice] = useState(1);
  const [categorySelected, setCategorySelected] = useState(-1);
  const categories = [
    "Remote Patient Monitoring",
    "Prosthodontics",
    "Virtual Examination Room",
  ];

  useEffect(() => {
    productsController.getProducts().then((response) => {
      setProductsList(response.data);

      let max = 0;
      response.data.map((pr, i) => {
        if (pr.price > max) {
          max = pr.price;
        }
      });
      setMaxPrice(max);
    });
  }, []);

  return (
    <div className="">
      <NavBar selectedTab="products" />

      <div className="container-fluid row d-flex justify-content-center">
        <div className="col-12 search-bar d-flex align-items-center">
          <input
            type="text"
            placeholder="Search: Place Holder: Company name, Device name , category of this devices"
            className="product-search-input"
          />
          <div className="search-icon">
            <SearchIcon />
          </div>
        </div>

        <SliderAnimation
          content={[
            {
              image: soulmed,
              title: categories[0],
              desciption: "Description 1",
              button: "Discover More",
            },
            {
              image: laptop1,
              title: categories[1],
              desciption: "Description 2",
              button: "Discover More",
            },
            {
              image: laptop1,
              title: categories[2],
              desciption: "Description 3",
              button: "Discover More",
            },
          ]}
        />

        <div className="products-categories w-100 col-12 mt-5 d-flex flex-wrap justify-content-center">
          {categories.map((category) => {
            return (
              <div className="category-card mx-2 mt-2 col-4">
                <h4>{category}</h4>
                <br />
                <div className="d-flex flex-wrap">
                  <div className="d-flex w-50 h-100 justify-content-center align-items-center flex-column">
                    <img src={laptop1} alt="" className="product-image" />
                    <span>product1</span>
                  </div>
                  <div className="d-flex w-50 h-100 justify-content-center align-items-center  flex-column">
                    <img src={laptop1} alt="" className="product-image" />
                    <span>product2</span>
                  </div>
                  <div className="d-flex justify-content-center align-items-center w-50 flex-column">
                    <img src={laptop1} alt="" className="product-image" />
                    <span>product3</span>
                  </div>
                  <div className="d-flex justify-content-center align-items-center w-50 flex-column">
                    <img src={laptop1} alt="" className="product-image" />
                    <span>product4</span>
                  </div>
                </div>
              </div>
            );
          })}
        </div>
      </div>
      <br />
    </div>
  );
};

export default Product;
