import React, { useState, useRef } from "react";
import { Modal } from "react-bootstrap";
import SelectYears from "../inputs/select-years";
import "bootstrap/dist/css/bootstrap.min.css";
import "../css/registration-modal.css";
import { useForm, Controller } from "react-hook-form";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import { yupResolver } from "@hookform/resolvers/yup";
import * as yup from "yup";
import { util } from "../public/util";
import classNames from "classnames";

const RegistrationModal = ({ ...props }) => {
  const { register, handleSubmit, reset, formState, watch, control } = useForm({
    mode: "onChange",
  });
  const { errors, isDirty, isSubmitting, isValid } = formState;
  const password = useRef();
  password.current = watch("password", "");

  const onSubmitHandler = (data) => {
    props.onSubmit();
    reset();
  };

  const [termsChecked, setTermsChecked] = useState(false);
  const [showTermsModal, setShowTermsModal] = useState(false);
  const [showCustomDiv, setShowCustomDiv] = useState(false);
  const [gender, setGender] = useState("");
  const [role, setRole] = useState("");

  const options = [
    { value: "1", label: 'She: "Wish her a happy birthday!"' },
    {
      value: "2",
      label: 'He: "Wish him a happy birthday!"',
    },
    {
      value: "3",
      label: 'They: "Wish them a happy birthday!"',
    },
  ];
  const setUserGender = (gender) => {
    setGender(gender);
    setShowCustomDiv(false);
  };
  const customHandler = () => {
    setGender("custom");
    setShowCustomDiv(true);
  };
  const modal = (
    <Modal
      {...props}
      size="lg"
      aria-labelledby="contained-modal-title-vcenter"
      centered
      show={showTermsModal}
      onHide={() => setShowTermsModal(false)}
    >
      <Modal.Header closeButton>
        <Modal.Title id="contained-modal-title-vcenter">
          <div className="signup-title">
            <span className="title">Sign Up</span> <br />
            <span className="subtitle">It's quick and easy.</span>
          </div>
        </Modal.Title>
      </Modal.Header>
      <Modal.Body></Modal.Body>
    </Modal>
  );

  return (
    <>
      {modal}
      <Modal
        {...props}
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
        centered
      >
        <Modal.Header closeButton>
          <Modal.Title id="contained-modal-title-vcenter ">
            <div className="signup-title d-flex justify-content-center w-100">
              <span className="title">Register Now</span> <br />
            </div>
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <div className="signup-form">
            <form onSubmit={handleSubmit(onSubmitHandler)}>
              <div className="form-group d-flex justify-content-between">
                {/* <div className="flex-column d-flex">
<select
  placeholder="prefix"
  {...register("prefix", {
    validate: (value) => value !== "",
  })}
>
  <option value="" selected disabled>
    Prefix
  </option>
  <option>Mr.</option>
  <option>Ms.</option>
  <option>Mrs.</option>
  <option>Dr.</option>
  <option>Prof.</option>
</select>
<div className="error">
  {errors.prefix && <p>Select a prefix</p>}
</div>
</div>*/}



              </div>









              <div className="form-group flex-column">
                <span>Register as</span>
                <div className="d-flex">
                  <div className="form-group form-check">
                    <input
                      type="radio"
                      value="patient"
                      checked={role === "patient"}
                      onClick={() => setRole("patient")}
                      {...register("role", {
                        validate: (value) => role !== "",
                      })}
                    />
                    <label>Patient</label>
                  </div>
                  <div className="form-group form-check">
                    <input
                      type="radio"
                      value="doctor"
                      checked={role === "doctor"}
                      onClick={() => setRole("doctor")}
                      {...register("role", {
                        validate: (value) => role !== "",
                      })}
                    />
                    <label>Doctor</label>
                  </div>
                  <div className="form-group form-check">
                    <input
                      type="radio"
                      checked={role === "organization"}
                      value="organization"
                      onClick={() => setRole("organization")}
                      {...register("role", {
                        validate: (value) => role !== "",
                      })}
                    />
                    <label>Organization</label>
                  </div>
                </div>
              </div>



              <br />

              <div className="d-flex align-items-center justify-content-center">
                <div className="signup-btn d-flex justify-content-center">
                  <input
                    className="btn"
                    type="submit"
                    disabled={!isDirty || !isValid}
                    value="Register"
                  />
                </div>
              </div>
            </form>
          </div>
        </Modal.Body>
      </Modal>
    </>
  );
};

export default RegistrationModal;
