import React, { useState, useRef } from "react";
import { Modal } from "react-bootstrap";
import SelectYears from "../inputs/select-years";
import "bootstrap/dist/css/bootstrap.min.css";
import "../css/registration-modal.css";
import { useForm, Controller } from "react-hook-form";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

const RegistrationModal = ({ ...props }) => {
  const { register, handleSubmit, reset, formState, watch, control } =
    useForm();
  const { errors } = formState;
  const password = useRef();
  password.current = watch("password", "");

  const onSubmitHandler = (data) => {
    console.log({ data });
    reset();
  };

  return (
    <Modal
      {...props}
      size="lg"
      aria-labelledby="contained-modal-title-vcenter"
      centered
    >
      <Modal.Header closeButton>
        <Modal.Title id="contained-modal-title-vcenter">
          <div className="signup-title">
            <span className="title">Sign Up</span> <br />
            <span className="subtitle">It's quick and easy.</span>
          </div>
        </Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <div className="signup-form">
          <form onSubmit={handleSubmit(onSubmitHandler)}>
            <div className="common-fields">
              <div className="form-group d-flex justify-content-between">
                <select placeholder="Prefix">
                  <option selected disabled>
                    Prefix
                  </option>
                  <option>Mr.</option>
                  <option>Ms.</option>
                  <option>Mrs.</option>
                  <option>Dr.</option>
                  <option>Prof.</option>
                </select>
                <div className="flex-column d-flex">
                  <input
                    type="text"
                    placeholder="First Name"
                    className="input"
                    required
                    {...register("firstName", {
                      pattern: /^[A-Za-z]+$/i,
                    })}
                  />
                  <div className="error">
                    {errors.firstName?.type === "pattern" &&
                      "Invalid Format, special characters are not allowed"}
                  </div>
                </div>
                <div className="flex-column d-flex">
                  <input
                    type="text"
                    placeholder="Last Name"
                    className="input"
                    {...register("lastName", {
                      pattern: /^[A-Za-z]+$/i,
                    })}
                  />
                  <div className="error">
                    {errors.lastName?.type === "pattern" &&
                      "Invalid Format, special characters are not allowed"}
                  </div>
                </div>
              </div>

              <div className="form-group d-flex flex-column">
                <input
                  type="email"
                  placeholder="Email"
                  required
                  className="input full-width"
                  {...register("email", {
                    pattern: /^[\w-\.]+@([\w-]+\.)+[\w-]{2,4}$/g,
                  })}
                />
                <div className="error">
                  {errors.email?.type === "pattern" && "Invalid email format"}
                </div>
              </div>
              <div className="form-group d-flex flex-column">
                <input
                  type="password"
                  placeholder="New Password"
                  className="input full-width"
                  {...register("password", {
                    pattern:
                      /^(?=.*\d)(?=.*[!@#$%^&*])(?=.*[a-z])(?=.*[A-Z]).{8}$/,
                  })}
                />
                <div className="error">
                  {errors.password?.type === "pattern" &&
                    "Invalid format, please retype password"}
                </div>
              </div>

              <div className="form-group d-flex flex-column">
                <input
                  type="password"
                  placeholder="New Password"
                  className="input full-width"
                  {...register("password", {
                    pattern:
                      /^(?=.*\d)(?=.*[!@#$%^&*])(?=.*[a-z])(?=.*[A-Z]).{8}$/,
                  })}
                />
                <div className="error">
                  {errors.password?.type === "pattern" &&
                    "Invalid format, please retype password"}
                </div>
              </div>

              <div className="form-group d-flex flex-column">
                <input
                  type="password"
                  placeholder="Confirm Password"
                  className="input full-width"
                  ref={register("confirm_password", {
                    validate: (value) =>
                      value === password.current ||
                      "The passwords do not match",
                  })}
                />
                <div className="error">
                  {errors.password_repeat && (
                    <p>{errors.password_repeat.message}</p>
                  )}
                </div>
              </div>

              <div>
                <Controller
                  {...register("dob")}
                  control={control}
                  defaultValue={new Date()}
                  render={({ field: { onChange, value } }) => {
                    return (
                      <DatePicker
                        className="dob full-width"
                        onChange={onChange}
                        selected={value}
                        placeholderText="Enter your birth date"
                      />
                    );
                  }}
                />
              </div>

              <div className="form-group">
                <select className="full-width">
                  <option disabled selected>Select your country</option>
                  <option>Lebanon</option>
                  <option>US</option>
                  <option>France</option>
                </select>
              </div>
              <div className="form-group">
                <input
                  type="tel"
                  className="input full-width"
                  pattern="[0-9]{3}-[0-9]{2}-[0-9]{3}"
                  placeholder="Phone Number"
                />
              </div>
            </div>

            <div className="d-flex align-items-center justify-content-center">
              <div className="signup-btn d-flex justify-content-center">
                <input className="btn" type="submit" />
              </div>
            </div>
          </form>
          <br />
          <span>
            By clicking Sign Up, you agree to our <a href="">Terms</a>,{" "}
            <a href="">Data Policy</a> and <a href="">Cookie Policy</a>. You may
            receive SMS notifications from us and can opt out at any time.
          </span>
        </div>
      </Modal.Body>
    </Modal>
  );
};

export default RegistrationModal;
