import i18n from "i18next";
import { initReactI18next } from "react-i18next";

import Backend from "i18next-http-backend";
import LanguageDetector from "i18next-browser-languagedetector";
// don't want to use this?
// have a look at the Quick start guide
// for passing in lng and translations on init

const getTranslationsEndpoint =
  "http://localhost:5000/{{lng}}";
console.log(getTranslationsEndpoint)
i18n
  // load translation using http -> see /public/locales (i.e. https://github.com/i18next/react-i18next/tree/master/example/react/public/locales)
  // learn more: https://github.com/i18next/i18next-http-backend
  // want your translations to be loaded from a professional CDN? => https://github.com/locize/react-tutorial#step-2---use-the-locize-cdn
  .use(Backend)
  // detect user language
  // learn more: https://github.com/i18next/i18next-browser-languageDetector
  .use(LanguageDetector)
  // pass the i18n instance to react-i18next.
  .use(initReactI18next)
  // init i18next
  // for all options read: https://www.i18next.com/overview/configuration-options
  .init({
    lng: "en",
    fallbackLng: "en",
    debug: true,
    defaultNS: "translation",
    ns: "translation",
    initImmediate: false, // set initImmediate false -> init method finished only when all resources/translation finish loading (async behaviour)

    interpolation: {
      escapeValue: false, // not needed for react as it escapes by default
    },

    backend: {
      loadPath: "http://localhost:3000/public/locales/{{lng}}/{{ns}}.json",
      addPath:"/locales/all.json",
      withCredentials: false,
      crossDomain: false,
      requestOptions: {
        method: "GET",
        mode: "no-cors",
        crossDomain:true,
        credentials: 'same-origin',
        cache: 'default'

      },
      parse: function(data) { return data.replace(/a/g, ''); },

      parsePayload: function(namespace, key, fallbackValue) { return { key } },
      customHeaders: {
        authorization: 'foo',
        // ...
      },

    },
  });

export default i18n;
