import React, { useState, useEffect } from "react";
import FullCalendar from "@fullcalendar/react"; // must go before plugins
import dayGridPlugin from "@fullcalendar/daygrid"; // a plugin!
import timeGridPlugin from "@fullcalendar/timegrid";
import interactionPlugin from "@fullcalendar/interaction"; // needed for dayClick
import { Modal } from "react-bootstrap";
import "../css/calendar.css";
import { format } from "date-fns";
import moment from "moment";
import { util } from "../public/util";
import "../scripts/calendar";
import { doctorController } from "../controllers/doctor";

const Calendar = ({ ...props }) => {
  const [newEvent, setNewEvent] = useState({});
  const [appointmentText, setAppointmentText] = useState("");
  const [startDate, setStartDate] = useState(new Date());
  const [datetime, setDatetime] = useState("");
  const [added, setAdded] = useState(false);
  const [showAppointmentModal, setShowAppointmentModal] = useState(false);
  const [appointmentsList, setAppointmentsList] = useState([]);
  const [scheduleList, setScheduleList] = useState([]);
  const [loading, isLoading] = useState(true);
  const [data, setData] = useState({
    allFavs: [],
    favObj: {},
    start: "",
    end: "",
    view: "",
    filledIn: false,
    events: [],
  });
  useEffect(() => {
    doctorController.getAllDoctorAppointments(5).then((apptms) => {
      doctorController
        .getDoctorSchedule(5)
        .then((schedule) => {
          let list = schedule.data;
          for (let i = 0; i < list.length; i++) {
            list[i]["is_schedule"] = true;
          }
          let tempData = data;
          tempData.events = [...apptms.data, ...schedule.data];
          setData(tempData);
        })
        .then(() => {
          isLoading(false);
        });
    });
  }, []);

  const [showEventsListModal, setShowEventsListModal] = useState(false);
  const [scheduleEventSelected, setScheduleEventSelected] = useState(false);

  const [bookAppointmentSelected, setBookAppointmentSelected] = useState(false);
  const handleBookApptBtn = () => {
    setBookAppointmentSelected(true);
    setShowAppointmentModal(true);
  };

  const createEvent = () => {
    let tempData = data;
    let events = tempData.events;
    if (!bookAppointmentSelected) {
      let endTime =
        data.view === "timeGridWeek" || data.view === "timeGridDay"
          ? new Date(tempData.start.split("+")[0])
          : new Date(tempData.start + " " + datetime);

      endTime.setMinutes(endTime.getMinutes() + 30);
      endTime = moment(endTime).format("YYYY-MM-DDTkk:mm:ss");

      let newEvent = {
        title: appointmentText,
        id: data.events.length + 1,
        start:
          data.view === "timeGridWeek" || data.view === "timeGridDay"
            ? tempData.start.split("+")[0]
            : tempData.start + "T" + datetime,
        end: endTime,
      };

      if (util.isOverlapping(newEvent, data.events)) {
        alert("Appointment Overlapping, Try another time");
      } else {
        events = [...events, newEvent];
      }
    } else {
      let endTime = new Date(
        datetime.split("T")[0] + " " + datetime.split("T")[1]
      );
      endTime.setMinutes(endTime.getMinutes() + 30);

      endTime = moment(endTime).format("YYYY-MM-DDTkk:mm:ss");

      let newEvent = {
        title: appointmentText,
        start: datetime,
        end: endTime,
        id: data.events.length + 1,
      };
      if (util.isOverlapping(newEvent, data.events)) {
        alert("Appointment Overlapping, Try another time");
      } else {
        events = [...events, newEvent];
      }
    }
    setDatetime("");
    setAppointmentText("");
    tempData.events = events;
    setData(tempData);
    setShowAppointmentModal(false);
    setBookAppointmentSelected(false);
  };

  const handleDateSelect = (selectInfo) => {
    if (props.allowEdit) {
      if (
        new Date(selectInfo.startStr) - new Date() < 0 &&
        !util.isToday(new Date(selectInfo.startStr))
      ) {
        alert("Cannot add on this day");
      } else {
        let tempData = data;
        tempData.start = selectInfo.startStr;
        tempData.end = selectInfo.endStr;
        tempData.view = selectInfo.view.type;
        setShowAppointmentModal(true);
        setData(tempData);
      }
    } else {
      if (selectInfo.view.type === "dayGridMonth") {
        let tempData = data;
        tempData.events = props.events;
        tempData.start = selectInfo.startStr;
        tempData.end = selectInfo.endStr;
        setData(tempData);

        setShowEventsListModal(true);
      }
    }
  };

  const handleEventClick = (clickInfo) => {
    if (props.allowEdit) {
      let tempData = data;
      tempData.start = clickInfo.event._instance.range.start;
      tempData.end = clickInfo.event._instance.range.end;
      setData(tempData);
      setScheduleEventSelected(true);
      setShowAppointmentModal(true);
    }
  };

  const workSpec = [
    {
      daysOfWeek: [1, 2, 3, 4],
      startTime: "08:00",
      endTime: "13:00",
    },
    {
      daysOfWeek: [5, 6],
      startTime: "09:00",
      endTime: "14:00",
      backgroundColor: "red",
    },
  ];

  const workMin = workSpec
    .map((item) => item.startTime)
    .sort()
    .shift();
  const workMax = workSpec
    .map((item) => item.endTime)
    .sort()
    .pop();
  const workDays = [...new Set(workSpec.flatMap((item) => item.daysOfWeek))];
  const hideDays = [...Array(7).keys()].filter(
    (day) => !workDays.includes(day)
  );

  let calendar = (
    <FullCalendar
      headerToolbar={{
        left: "prev,next,today",
        center: "title",
        right: "timeGridWeek,timeGridDay,dayGridMonth",
      }}
      initialView="timeGridWeek"
      height={500}
      selectable={true}
      select={handleDateSelect}
      selectMirror={true}
      plugins={[dayGridPlugin, timeGridPlugin, interactionPlugin]}
      weekends={true}
      events={data.events}
      allDaySlot={false}
      eventDidMount={function (event) {
        console.log(event);
        if (event.event._def.extendedProps.is_schedule) {
          let details = event.event._def.extendedProps;
          event.el.firstChild.parentElement.classList.add(
            details.type === 0
              ? "physical_appointment"
              : details.type === 1
              ? "virtual_appointment"
              : "pv_appointment"
          );
          event.el.firstChild.parentElement.children[0].classList.add("none");

          //     event
        }
      }}
      slotDuration={"00:30"}
      eventConstraint="businessHours"
      aspectRatio={5}
      eventClick={handleEventClick}
    />
  );

  const hideModal = () => {
    setShowAppointmentModal(false);
    let tempData = data;
    tempData.start = "";
    setData(tempData);
    setBookAppointmentSelected(false);
  };

  return (
    !loading && <>
      {calendar}
      <br />
      <div className="d-flex justify-content-between">
        <div className="d-flex">
          <div className="physical_appointment scale_key"></div>
          <span>Physical Appointment</span>
        </div>
        <div className="d-flex">
          <div className="virtual_appointment scale_key"></div>
          <span>Virtual Appointment</span>
        </div>
        <div className="d-flex">
          <div className="pv_appointment scale_key"></div>
          <span>Physical {"&"} Virtual Appointment</span>
        </div>
      </div>
      <br />
      {props.allowEdit && (
        <button onClick={() => handleBookApptBtn()}>Book Appointment</button>
      )}

      <Modal
        show={showAppointmentModal}
        onHide={() => hideModal()}
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
        centered
      >
        <Modal.Header closeButton>
          <Modal.Title id="contained-modal-title-vcenter">
            <div className="signup-title">
              <span className="title">Add Appointment</span> <br />
              <span className="subtitle">It's quick and easy.</span>
            </div>
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <div className="d-flex flex-column">
            <input
              type="text"
              placeholder="Appoitment description"
              className="input"
              onChange={(e) => setAppointmentText(e.target.value)}
            />

            <div className="d-flex justify-content-between align-items-center p-3 input">
              <span>Appointment Type</span>

              <div className="d-flex align-items-center">
                <input type="radio" value="physical" name="apmt_type" />
                <label>Physical</label>
              </div>
              <div className="d-flex align-items-center mx-2">
                <input type="radio" value="virtual" name="apmt_type" />
                <label>Virtual</label>
              </div>
            </div>

            <input
              className="input"
              defaultValue={startDate}
              type={
                bookAppointmentSelected
                  ? "datetime-local"
                  : data.view === "dayGridMonth" || scheduleEventSelected
                  ? "time"
                  : "hidden"
              }
              min={data.startStr}
              max={data.endStr}
              step="1"
              onChange={(e) => setDatetime(e.target.value)}
            />
          </div>
        </Modal.Body>
        <Modal.Footer>
          <button onClick={() => createEvent()}>Add</button>
        </Modal.Footer>
      </Modal>

      <Modal
        show={showEventsListModal}
        onHide={() => setShowEventsListModal(false)}
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
        centered
      >
        <Modal.Header closeButton>
          <Modal.Title id="contained-modal-title-vcenter">
            <div className="signup-title">
              <span className="title">
                All Appointments for{" "}
                {moment(new Date(data.start)).format("DD-MM-YYYY").toString()}
              </span>{" "}
              <br />
            </div>
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {data.events.filter((aptm) =>
            util.sameDay(new Date(aptm.start), new Date(data.start))
          ).length === 0 && <div>No Events on this day</div>}
          {data.events
            .filter((aptm) =>
              util.sameDay(new Date(aptm.start), new Date(data.start))
            )
            .map((apmt) => {
              return (
                <div className="d-flex flex-column">
                  <span>{apmt.title}</span>
                  <div className="d-flex">
                    <span>Start Time</span>
                    <span>
                      {new Date(apmt.start).getHours()}:
                      {new Date(apmt.start).getMinutes().toString().length === 1
                        ? "0" + new Date(apmt.start).getMinutes()
                        : new Date(apmt.start).getMinutes()}
                    </span>
                  </div>
                  <div className="d-flex">
                    <span>End Time</span>
                    <span>
                      {new Date(apmt.end).getHours()}:
                      {new Date(apmt.end).getMinutes().toString().length === 1
                        ? "0" + new Date(apmt.end).getMinutes()
                        : new Date(apmt.end).getMinutes()}
                    </span>
                  </div>
                </div>
              );
            })}
        </Modal.Body>
      </Modal>
    </>
  );
};

export default Calendar;
