import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { doctorController } from "../../controllers/doctor";
import Layout from "./layout";
import "../../css/schedule-settings.css";
import moment from "moment";
import { useNavigate } from "react-router-dom";
import { util } from "../../public/util";

const ScheduleSettings = () => {
  const { id } = useParams();
  const [loading, setLoading] = useState(true);
  const [doctorData, setDoctorData] = useState({
    id: -1,
    name: "",
    country: "",
    profession_fk: -1,
    city: "",
    Price: "",
    location: [],
  });
  const [doctorSchedule, setDoctorSchedule] = useState([]);
  const [timingIndexCount, setTimingIndexCount] = useState(-1);
  useEffect(() => {
    doctorController
      .getDoctorInfo(id)
      .then((response) => {
        setDoctorData(response.data);
      })
      .then(() => {
        doctorController
          .getDoctorSchedule(id)
          .then((res) => {
            setDoctorSchedule(res.data);
            setTimingIndexCount(res.data.length + 1);
          })
          .then(() => {
            setLoading(false);
          });
      });
  }, []);

  const daysOfWeek = ["Mon", "Tue", "Wed", "Thu", "Fri", "Sat", "Sun"];

  console.log(timingIndexCount);

  const [timingArray, setTimingarray] = useState([]);
  const addTiming = () => {
    let temp = timingArray;
    temp = [...temp, { id: timingIndexCount, start: "", end: "", type: 0 }];
    setTimingarray(temp);

    setTimingIndexCount(timingIndexCount + 1);
     console.log(timingArray)
  };
  const deleteTiming = (index) => {
    let temp = timingArray;
    temp = timingArray.filter((timing) => timing.id !== index);
    setTimingarray(temp);
  };

  const modifyStartTiming = (id, index, value) => {
    let temp = timingArray;
    let tempTime = temp[index];
    temp = timingArray.filter(
      (timing) =>
        timing.id !== id ||
        (timing.id !== id && timing.start === "") ||
        (timing.id !== id && timing.end === "")
    );

    tempTime.start = value;
    temp = [...temp.slice(0, index), tempTime, ...temp.slice(index)];
    setTimingarray(temp);
  };
  const modifyEndTiming = (id, index, value) => {
    let temp = timingArray;
    let tempTime = temp[index];
    temp = timingArray.filter(
      (timing) =>
        timing.id !== id ||
        (timing.id !== id && timing.start === "") ||
        (timing.id !== id && timing.end === "")
    );

    tempTime.end = value;
    temp = [...temp.slice(0, index), tempTime, ...temp.slice(index)];
    setTimingarray(temp);
  };
  const modifyAppointmentsType = (id, index, value) => {
    let temp = timingArray;
    let tempTime = temp[index];
    temp = timingArray.filter(
      (timing) =>
        timing.id !== id ||
        (timing.id !== id && timing.start === "") ||
        (timing.id !== id && timing.end === "")
    );

    tempTime.type = value;
    temp = [...temp.slice(0, index), tempTime, ...temp.slice(index)];
    setTimingarray(temp);
  };

  const [location, setLocation] = useState("");
  const [daysSelected, setDaysSelected] = useState([]);
  const navigate = useNavigate();

  const updateSchedule = () => {
    let overlapping = false;
    let emptyFields = false;
    let validIntervals = true;
    let newTimingDates = [];
    let count = 0;

    for (let i = 0; i < timingArray.length; i++) {
      for (let j = 0; j < daysSelected.length; j++) {
        newTimingDates = [
          ...newTimingDates,
          {
            id: timingArray[i].id,
            doctor_fk: parseInt(id),
            start:
              moment(
                util.getDayDate(daysOfWeek.indexOf(daysSelected[j]))
              ).format("YYYY-MM-DD") +
              "T" +
              timingArray[i].start,
            end:
              moment(
                util.getDayDate(daysOfWeek.indexOf(daysSelected[j]))
              ).format("YYYY-MM-DD") +
              "T" +
              timingArray[i].end,
            location: location,
            type: parseInt(timingArray[i].type),
          },
        ];

        count++;
      }
    }
    console.log(newTimingDates);

    for (let i = 0; i < newTimingDates.length; i++) {
      if (util.isOverlapping(newTimingDates[i], newTimingDates)) {
        overlapping = true;
      }
      if (
        newTimingDates[i].start === "" ||
        newTimingDates[i].end === "" ||
        newTimingDates[i].type === ""
      ) {
        emptyFields = true;
      }
      if (
        !util.isValidTimingInterval(
          newTimingDates[i].start,
          newTimingDates[i].end
        )
      ) {
        validIntervals = false;
      }
    }
    let success = true;
    if (overlapping) {
      alert("Two intervals are overlapping");
      success = false;
    }
    if (emptyFields || location === "") {
      alert("Please fill all fields");
      success = false;
    }
    if (daysSelected.length === 0) {
      alert("Select at least one day");
      success = false;
    }
    if (!validIntervals) {
      alert("Invalid Timings");
      success = false;
    }
    if (success) {
      for (let j = 0; j < newTimingDates.length; j++) {
        doctorController.addDoctorAppointmentSchedule(newTimingDates[j]);
      }
      alert("Schedule Updated");
      //   navigate("/doctor/dashboard/");
    }
  };

  const handleChange = (e, data) => {
    const { name, checked } = e.target;
    if (checked) {
      // if cheked and selectall checkbox add all fileds to selectedList
      if (name === "allSelect") {
        let temp = daysOfWeek;
        setDaysSelected(temp);
      } else {
        // if cheked and specific checkbox add specific field to selectedList
        setDaysSelected([...daysSelected, name]);
      }
    } else {
      // if uncheked and selectall checkbox add remove all fileds from selectedList
      if (name === "allSelect") {
        setDaysSelected([]);
      } else {
        // if uncheked and specific checkbox remove specific field from selectedList
        let tempSelected = daysSelected.filter((item, i) => item !== name);

        setDaysSelected(tempSelected);
      }
    }
  };

  return (
    <Layout>
      {!loading && (
        <div className="d-flex justify-content-center">
          <div className="col-8 schedule-div">
            <h3>
              Weekly Schedule (From{" "}
              {util.getDayDate(0).toString().substring(0, 15)} into{" "}
              {util.getDayDate(6).toString().substring(0, 15)})
            </h3>
            <br />
            <div className="d-flex flex-column">
              <span>Location</span>
              <select onChange={(e) => setLocation(e.target.value)}>
                <option selected disabled>
                  Select your location
                </option>
                {doctorData.location.map((l, i) => {
                  return <option key={"location" + i}>{l}</option>;
                })}
              </select>
            </div>
            <br />
            <div className="d-flex flex-column">
              <span>Appointment Schedule</span>
              <input
                type="button"
                className="input"
                value="Add Timing"
                onClick={() => addTiming()}
              />
              <div className="d-flex w-100 flex-column justify-content-center">
                {timingArray.map((time, index) => {
                  return (
                    <div
                      key={"time" + index}
                      className="d-flex  justify-content-between"
                    >
                      <input
                        className="input"
                        placeholder={"Start Time "}
                        type="time"
                        value={timingArray[index].start}
                        onChange={(e) =>
                          modifyStartTiming(time.id, index, e.target.value)
                        }
                      />
                      <input
                        className="input"
                        placeholder="End Time"
                        type="time"
                        value={timingArray[index].end}
                        onChange={(e) =>
                          modifyEndTiming(time.id, index, e.target.value)
                        }
                      />
                      <select
                        onChange={(e) =>
                          modifyAppointmentsType(
                            time.id,

                            index,
                            e.target.value
                          )
                        }
                        value={timingArray[index].type}
                        className="input"
                      >
                        <option selected disabled>
                          Select type
                        </option>
                        <option value={0}>Physical</option>
                        <option value={1}>Virtual</option>
                        <option value={2}>Physical or Virtual</option>
                        <option value={3}>Meeting</option>
                        <option value={4}>Lunch Break</option>
                      </select>
                      <input
                        type="button"
                        value={"X"}
                        className="input close"
                        onClick={() => deleteTiming(time.id)}
                      />
                    </div>
                  );
                })}
              </div>
              <br />
              <div className="d-flex flex-column">
                <label>Select your preffered days</label>
                <div className="d-flex  align-items-center justify-content-center mx-4">
                  <input
                    name="allSelect"
                    onChange={(e) => handleChange(e)}
                    type="checkbox"
                    checked={daysSelected.length === 7}
                  />
                  <label>Select All</label>
                </div>
                <br />
                <div className="d-flex  align-items-center justify-content-center flex-wrap">
                  {daysOfWeek.map((day, i) => {
                    return (
                      <div
                        key={"day" + i}
                        className="d-flex input align-items-center justify-content-center mx-4"
                      >
                        <input
                          name={day}
                          onChange={(e) => handleChange(e)}
                          type="checkbox"
                          value={day}
                          checked={daysSelected.some((item, j) => item === day)}
                        />
                        <label>{day}</label>
                      </div>
                    );
                  })}
                </div>
              </div>
              <br />
              <div className="d-flex flex-column">
                <input
                  type="button"
                  value={"Validate"}
                  className="input"
                  onClick={() => updateSchedule()}
                />
              </div>
            </div>
          </div>
        </div>
      )}
    </Layout>
  );
};

export default ScheduleSettings;
