import React, { useState } from "react";
import { Modal } from "react-bootstrap";
import SelectYears from "../inputs/select-years";
import "bootstrap/dist/css/bootstrap.min.css";
import "../css/registration-modal.css";
import { useForm } from "react-hook-form";

const RegistrationModal = ({ ...props }) => {
  const {
    register,
    handleSubmit,
    watch,
    formState: { errors },
  } = useForm();
  const onSubmit = (data) => console.log(data);
  const [showCustomGender, setShowCustomGender] = useState(false);
  const [role, setRole] = useState("");
  const [completeRegistration, setCompleteRegistration] = useState(false);
  const doctorExtraStep = (e) => {
    setCompleteRegistration(true);
    e.preventDefault();
  };
  const backButtonAction = (e) => {
    setCompleteRegistration(false);
    setRole("");
    e.preventDefault();
  };

  const [userInfo, setUserInfo] = useState({
    prefix: "",
    firstName: "",
    lastName: "",
    dob: "",
    phone_number: "",
    location: "",
    email: "",
    password: "",
    doctorInfo: {
      graduation_year: "",
      years_of_experirence: "",
      speciality: "",
      clinic_hospital: "",
      doctorial_council_number: "",
      certifications: "",
      picture_path: "",
    },
  });

  const updateUserInfo = (key, value, role) => {};

  return (
    <Modal
      {...props}
      size="lg"
      aria-labelledby="contained-modal-title-vcenter"
      centered
    >
      <Modal.Header closeButton>
        <Modal.Title id="contained-modal-title-vcenter">
          <div className="signup-title">
            <span className="title">Sign Up</span> <br />
            <span className="subtitle">It's quick and easy.</span>
          </div>
        </Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <div className="signup-form">
          <form onSubmit={handleSubmit(onSubmit)}>
            <div className="common-fields">
              <div className="form-group d-flex justify-content-between">
                <select placeholder="Prefix">
                  <option selected disabled>
                    Prefix
                  </option>
                  <option>Mr.</option>
                  <option>Ms.</option>
                  <option>Mrs.</option>
                  <option>Dr.</option>
                  <option>Prof.</option>
                </select>
                <input
                  type="text"
                  placeholder="First Name"
                  className="input"
                  {...register("firstName", {
                    required: true,
                    pattern: /^[A-Za-z]+$/i,
                  })}
                />
                {errors.firstName?.type === "required" &&
                  "First name is required"}

                <input
                  type="text"
                  placeholder="Last Name"
                  className="input"
                  {...register("lastName", {
                    required: true,
                    pattern: /^[A-Za-z]+$/i,
                  })}
                />
                {errors.lastName?.type === "required" &&
                  "Last name is required"}
              </div>

              <div className="form-group d-flex">
                <input
                  type="email"
                  placeholder="Email"
                  className="input full-width"
                />
              </div>
              <div className="form-group">
                <input
                  type="password"
                  placeholder="New Password"
                  className="input full-width"
                />
              </div>
              <div className="form-group">
                <input
                  type="text"
                  placeholder="Country"
                  className="input full-width"
                />
              </div>
              <div className="form-group">
                <input
                  type="tel"
                  className="input full-width"
                  pattern="[0-9]{3}-[0-9]{2}-[0-9]{3}"
                  placeholder="Phone Number"
                />
              </div>
            </div>

            <div className="d-flex align-items-center justify-content-center">
              <div className="signup-btn d-flex justify-content-center">
                <input className="btn" type="submit" />
              </div>
            </div>
          </form>
          <br />
          <span>
            By clicking Sign Up, you agree to our <a href="">Terms</a>,{" "}
            <a href="">Data Policy</a> and <a href="">Cookie Policy</a>. You may
            receive SMS notifications from us and can opt out at any time.
          </span>
        </div>
      </Modal.Body>
    </Modal>
  );
};

export default RegistrationModal;
