import { useForm, Controller } from "react-hook-form";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import { yupResolver } from "@hookform/resolvers/yup";
import * as yup from "yup";
import { util } from "../public/util";
import classNames from "classnames";

const RegistrationModal = ({ ...props }) => {
  const { register, handleSubmit, reset, formState, watch, control } = useForm({
    mode: "onChange",
  });
  const { errors, isDirty, isSubmitting, isValid } = formState;
  const password = useRef();
  password.current = watch("password", "");

  const onSubmitHandler = (data) => {
    console.log({ data });

    reset();
  };

  const [termsChecked, setTermsChecked] = useState(false);
  const [showTermsModal, setShowTermsModal] = useState(false);
  const [showCustomDiv, setShowCustomDiv] = useState(false);
  const modal = (
    <Modal
      {...props}
      size="lg"
      aria-labelledby="contained-modal-title-vcenter"
      centered
      show={showTermsModal}
      onHide={() => setShowTermsModal(false)}
    >
      <Modal.Header closeButton>
        <Modal.Title id="contained-modal-title-vcenter">
          <div className="signup-title">
            <span className="title">Sign Up</span> <br />
            <span className="subtitle">It's quick and easy.</span>
          </div>
        </Modal.Title>
      </Modal.Header>
      <Modal.Body></Modal.Body>
    </Modal>
  );

  return (
    <>
      {modal}
      <Modal
        {...props}
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
        centered
      >
        <Modal.Header closeButton>
          <Modal.Title id="contained-modal-title-vcenter ">
            <div className="signup-title d-flex justify-content-center w-100">
              <span className="title">Register Now</span> <br />
            </div>
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <div className="signup-form">
            <form onSubmit={handleSubmit(onSubmitHandler)}>
              <div className="form-group d-flex justify-content-between">
                {/* <div className="flex-column d-flex">
                <select
                  placeholder="prefix"
                  {...register("prefix", {
                    validate: (value) => value !== "",
                  })}
                >
                  <option value="" selected disabled>
                    Prefix
                  </option>
                  <option>Mr.</option>
                  <option>Ms.</option>
                  <option>Mrs.</option>
                  <option>Dr.</option>
                  <option>Prof.</option>
                </select>
                <div className="error">
                  {errors.prefix && <p>Select a prefix</p>}
                </div>
                </div>*/}

                <div className="form-group d-flex flex-column w-100">
                  <div className="d-flex">
                    <input
                      type="text"
                      placeholder="First Name"
                      className="input full-width"
                      {...register("firstName", {
                        pattern: /^[A-Za-z]+$/i,

                        validate: (value) =>
                          value !== "" && util.isValidName(value),
                      })}
                    />
                  </div>
                  <div className="error">
                    {errors.firstName?.type === "pattern" &&
                      "Invalid Format, special characters are not allowed"}
                  </div>
                </div>

                <div className="form-group d-flex flex-column w-100">
                  <div className="d-flex">
                    <input
                      type="text"
                      placeholder="Last Name"
                      className="input full-width"
                      {...register("lastName", {
                        pattern: /^[A-Za-z]+$/i,

                        validate: (value) =>
                          value !== "" && util.isValidName(value),
                      })}
                    />
                  </div>
                  <div className="error">
                    {errors.lastName?.type === "pattern" &&
                      "Invalid Format, special characters are not allowed"}
                  </div>
                </div>
              </div>

              <div className="form-group d-flex flex-column">
                <input
                  type="email"
                  placeholder="Email"
                  required
                  className="input full-width"
                  {...register("email", {
                    pattern: /^\w+([\\.-]?\w+)*@\w+([\\.-]?\w+)*(\.\w{2,3})+$/,
                    validate: (value) =>
                      value !== "" && util.isValidEmail(value),
                  })}
                />
                <div className="error">
                  {errors.email?.type === "pattern" && "Invalid email format"}
                </div>
              </div>

              <div className="form-group d-flex flex-column">
                <input
                  type="text"
                  placeholder="New Password"
                  className="input full-width"
                  {...register("password", {
                    pattern: /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)[a-zA-Z\d]{8,}$/,
                    validate: (value) =>
                      value !== "" && util.isValidPassword(value),
                  })}
                />
                <div className="error">
                  {errors.password?.type === "pattern" &&
                    "Invalid format, please retype password"}
                </div>
              </div>

              <div className="form-group d-flex flex-column">
                <input
                  type="password"
                  placeholder="Confirm Password"
                  className="input full-width"
                  {...register("confirm_password", {
                    validate: (value) =>
                      value === password.current ||
                      "The passwords do not match",
                  })}
                />
                <div className="error">
                  {errors.confirm_password && (
                    <p>{errors.confirm_password.message}</p>
                  )}
                </div>
              </div>

              <Controller
                control={control}
                {...register("dob", {
                  validate: (value) =>
                    value !== "" &&
                    value !== null &&
                    new Date() - new Date(value) >= 0,
                })}
                render={({ field: { onChange, value } }) => {
                  return (
                    <DatePicker
                      className="dob full-width"
                      onChange={onChange}
                      required
                      selected={value}
                      placeholderText="dd/mm/yyy"
                    />
                  );
                }}
              />

              <div className="form-group d-flex flex-column">
                <select
                  {...register("country", {
                    validate: (value) => value !== "",
                  })}
                  required
                  className="w-100"
                >
                  <option disabled selected value="">
                    Select your country
                  </option>
                  <option>Lebanon</option>
                  <option>US</option>
                  <option>France</option>
                </select>
                <div className="error">
                  {errors.country && <p>Select a country</p>}
                </div>
              </div>

              <div className="form-group d-flex flex-column">
                <div className="d-flex">
                  <input type="text" className="input" disabled value="+961" />
                  <input
                    type="text"
                    placeholder="Phone Number"
                    className="input full-width"
                    {...register("phone_number", {
                      pattern: /^[0-9]*$/,
                      validate: (value) =>
                        value !== "" && util.isValidPhoneNumber(value),
                    })}
                  />
                </div>
                <div className="error">
                  {errors.phone_number?.type === "pattern" && "Invalid format"}
                </div>
              </div>

              <div className="form-group flex-column">
                <span>Gender</span>
                <div className="d-flex">
                  <div className="form-group form-check">
                    <input
                      type="radio"
                      name="gender"
                      value="male"
                      onClick={() => setShowCustomDiv(false)}
                    />
                    <label>Male</label>
                  </div>
                  <div className="form-group form-check">
                    <input
                      type="radio"
                      name="gender"
                      value="female"
                      onClick={() => setShowCustomDiv(false)}
                    />
                    <label>Female</label>
                  </div>
                  <div className="form-group form-check">
                    <input
                      onClick={() => setShowCustomDiv(true)}
                      type="radio"
                      name="gender"
                      value="custom"
                    />
                    <label>Custom</label>
                  </div>
                </div>
                {showCustomDiv && (
                  <div className="d-flex flex-col">
                    <select>
                      <option disabled selected>
                        Select yout pronoun
                      </option>
                      <option>She: "Wish her a happy birthday!"</option>
                      <option>He: "Wish him a happy birthday!"</option>
                      <option>They: "Wish them a happy birthday!"</option>
                    </select>
                    <label>Your pronoun is visible to everyone</label>
                    <input
                      className="input full-width"
                      placeholder="Gender (optional)"
                    />
                  </div>
                )}

                <div className="error">
                  {errors.phone_number?.type === "pattern" && "Invalid format"}
                </div>
              </div>

              <br />
              <div className="d-flex flex-column justify-items-center w-100 p-4">
                <div className="d-flex align-items-center justify-content-center w-full p-3">
                  <input
                    type="checkbox"
                    value="terms_and_conditions"
                    className={classNames("check", {})}
                    onClick={(e) => setTermsChecked(e.target.checked)}
                    {...register("terms_and_conditions", {
                      validate: (value) => termsChecked === true,
                    })}
                  />
                  <label className="ms-2">
                    I agree with the{" "}
                    <a href="#" onClick={() => setShowTermsModal(true)}>
                      {" "}
                      terms and condition{" "}
                    </a>
                  </label>
                </div>
                <div className="form-group flex-column w-full">
                  <div className="d-flex align-items-center justify-content-center w-full">
                    <input type="checkbox" className="check" />
                    <label className="ms-2">
                      I want to receive <a href="#"> the new letter </a>
                    </label>
                  </div>
                </div>
              </div>

              <div className="d-flex align-items-center justify-content-center">
                <div className="signup-btn d-flex justify-content-center">
                  <input
                    className="btn"
                    type="submit"
                    disabled={!isDirty || !isValid}
                    value="Register"
                  />
                </div>
              </div>
            </form>
          </div>
        </Modal.Body>
      </Modal>
    </>
  );
};

export default RegistrationModal;
