import React, { useState, useRef } from "react";
import { Modal } from "react-bootstrap";
import SelectYears from "../inputs/select-years";
import "bootstrap/dist/css/bootstrap.min.css";
import "../css/registration-modal.css";
import { useForm, Controller } from "react-hook-form";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import { yupResolver } from "@hookform/resolvers/yup";
import * as yup from "yup";

const RegistrationModal = ({ ...props }) => {
  const schema = yup.object().shape({
    firstName: yup.string().email().required(),
  });
  const { register, handleSubmit, reset, formState, watch, control } = useForm({
    mode: "onChange",
  });
  const { errors, isDirty, isSubmitting, isValid } = formState;
  const password = useRef();
  password.current = watch("password", "");

  const onSubmitHandler = (data) => {
    console.log({ data });
    reset();
  };

  return (
    <Modal
      {...props}
      size="lg"
      aria-labelledby="contained-modal-title-vcenter"
      centered
    >
      <Modal.Header closeButton>
        <Modal.Title id="contained-modal-title-vcenter">
          <div className="signup-title">
            <span className="title">Sign Up</span> <br />
            <span className="subtitle">It's quick and easy.</span>
          </div>
        </Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <div className="signup-form">
          <form onSubmit={handleSubmit(onSubmitHandler)}>
            <div className="common-fields">
              <div className="form-group d-flex justify-content-between">
                <div className="flex-column d-flex">
                  <select
                    placeholder="prefix"
                    {...register("prefix", {
                      validate: (value) => value !== "",
                    })}
                  >
                    <option value="" selected disabled>
                      Prefix
                    </option>
                    <option>Mr.</option>
                    <option>Ms.</option>
                    <option>Mrs.</option>
                    <option>Dr.</option>
                    <option>Prof.</option>
                  </select>
                  <div className="error">
                    {errors.prefix && <p>Select a prefix</p>}
                  </div>
                </div>

                <div className="form-group d-flex flex-column">
                  <div className="d-flex">
                    <input
                      type="text"
                      placeholder="First Name"
                      className="input full-width"
                      {...register("firstName", {
                        pattern: /^[A-Za-z]+$/i,

                        validate: (value) =>
                          value !== "" 
                      })}
                    />
                  </div>
                  <div className="error">
                    {errors.firstName?.type === "pattern" &&
                      "Invalid Format, special characters are not allowed"}
                      </div>
                </div>

                <div className="flex-column d-flex">
                  <input
                    type="text"
                    placeholder="Last Name"
                    className="input"
                    {...register("lastName", {
                      pattern: /^[A-Za-z]+$/i,
                      validate: (value) =>
                        value !== "" 
                    })}
                  />
                  <div className="error">
                    {errors.lastName?.type === "pattern" &&
                      "Invalid Format, special characters are not allowed"}
                  </div>
                </div>
              </div>




            </div>

            <div className="d-flex align-items-center justify-content-center">
              <div className="signup-btn d-flex justify-content-center">
                <input
                  className="btn"
                  type="submit"
                  disabled={!isDirty || !isValid}
                />
              </div>
            </div>
          </form>
        </div>
      </Modal.Body>
    </Modal>
  );
};

export default RegistrationModal;
