import React, { useEffect, useState } from "react";
import NavBar from "../../components/nav-bar";
import "../../css/doctor-dashboard.css";
import Calendar from "../../inputs/calendar";
import { util } from "../../public/util";
import { doctorController } from "../../controllers/doctor";
import Layout from "./layout";
import DoctorAppointments from "../../components/doctor-appointments";
import DoctorRecentActivities from "../../components/doctor-recent-activities";

const DoctorDashboard = () => {
  const [appointmentsList, setAppointmentsList] = useState([]);
  const [scheduleList, setScheduleList] = useState([]);
  const [loading, isLoading] = useState(true);

  useEffect(() => {
    doctorController.getAllDoctorAppointments(5).then((response) => {
      setAppointmentsList(response);
    }).then(() => {
      doctorController.getDoctorSchedule(5).then((response) => {
        let list = response.data;
        for(let  i = 0 ; i < list.length ; i++){
          list[i]["is_schedule"] = true;
          list[i]["backgroundColor"] = list[i].type === 0 ? "#00FF00" : list[i].type === 1 ? "##FF0000" : "#00c0ef";
        }
        setScheduleList(list);

      }).then(() => {
        isLoading(false);
      })
    })

  }, []);



  return (
    <Layout doctorId={5}>
      <div className="row gx-0 justify-content-center">
        <div className="col-sm-10 col-md-10 col-lg-6 calendar">
          <h3>Calendar</h3>
          <br />
          {
            !loading &&  <Calendar
            isDoctor={true}
            allowEdit={false}
            events={appointmentsList}
            doctorId={5}
            schedule={scheduleList}
          />
          }

        </div>
        <div className="col-sm-10 col-md-10 col-lg-5 appointments ">
          <h3>Today's Appointments</h3>

          <DoctorAppointments appointments={appointmentsList} />
        </div>

        <br />
      </div>
      <br />

      <div className="d-flex justify-content-center">
        <div className="recent-activities col-lg-10 col-md-11 col-sm-11">
          <h3>Recent Activities</h3>
          <DoctorRecentActivities appointments={appointmentsList} />
        </div>
      </div>
    </Layout>
  );
};

export default DoctorDashboard;
