import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { productsController } from "../../controllers/product-page";
import "../../css/products-main.css";
import DownChevron from "../../icons/down-chevron";
import Close from "../../icons/close";
import SearchIcon from "../../icons/search-icon";

import {
  ReactiveBase,
  RangeSlider,
  SelectedFilters,
  ResultList,
  ReactiveList,
} from "@appbaseio/reactivesearch";
import classNames from "classnames";
import ProductsCustomPagination from "../../inputs/products-custom-pagination";
import NavBar from "../../components/nav-bar";
import PriceRange from "../../inputs/price-range";

const Product = ({ ...props }) => {
  let { productId } = useParams();
  const [productsList, setProductsList] = useState([]);
  const [maxPrice, setMaxPrice] = useState(1);
  const [categorySelected, setCategorySelected] = useState(-1);
  const categories = [
    "Remote Patient Monitoring",
    "Prosthodontics",
    "Virtual Examination Room",
  ];

  useEffect(() => {
    productsController.getProducts().then((response) => {
      setProductsList(response.data);

      let max = 0;
      response.data.map((pr, i) => {
        if (pr.price > max) {
          max = pr.price;
        }
      });
      setMaxPrice(max);
    });
  }, []);

  return (
    <div className="">
      <NavBar selectedTab="products" />

      <div className="filters container-fluid">
        <div className="row col-12">
          <div className="col-4 filter-keys">
            <div className="category-filter">
              <div
                data-bs-toggle="collapse"
                href="#categories-div"
                role="button"
                aria-expanded="false"
                aria-controls="categories-div"
              >
                  <span>Categories</span>
                <DownChevron />
              </div>
              <div id="categories-div" className="collapse">
                <div className="category">
                  <span>Remote Patient Monitoring</span>
                  <input type="radio" name="product-category" />
                </div>
                <div className="category">
                  <span>Remote Patient Monitoring</span>
                  <input type="radio" name="product-category" />
                </div>
                <div className="category">
                  <span>Remote Patient Monitoring</span>
                  <input type="radio" name="product-category" />
                </div>
              </div>

            </div>
            <div className="price-filter">
            <div
                data-bs-toggle="collapse"
                href="#price-div"
                role="button"
                aria-expanded="false"
                aria-controls="price-div"
              >
                  <span>Prices</span>
                <DownChevron />
              </div>
              <div id="price-div" className="collapse">
              <PriceRange min={0} max={maxPrice} />
              </div>
            </div>
          </div>
          <div className="col-6"></div>
        </div>
      </div>
    </div>
  );
};

export default Product;
