import React, { useEffect, useState } from "react";
import NavBar from "../../components/nav-bar";
import "../../css/doctor-dashboard.css";
import Calendar from "../../inputs/calendar";
import { util } from "../../public/util";
import { doctorController } from "../../controllers/doctor";
import Layout from "./layout";

const DoctorDashboard = () => {
  const [appointmentsList, setAppointmentsList] = useState([]);
  useEffect(() => {
    doctorController.getAllDoctorAppointments(5).then((response) => {
      setAppointmentsList(response.data);
    });
  }, []);

  return (
    <Layout>
      <div className="row">
        <div className="col d-flex justify-content-start">
          <Calendar isDoctor={true} allowEdit={false} events={appointmentsList} doctorId={5} />
        </div>
        <div className="col appointments d-flex flex-column align-items-start  justify-content-between">
          <div>
            {appointmentsList.map((apmt, i) => {
              return (
                util.sameDay(new Date(apmt.start), new Date()) && (
                  <div key={"apmt" + i} className="d-flex flex-column">
                    <span>{apmt.title}</span>
                    <div className="d-flex">
                      <span>Start Time</span>
                      <span>
                        {new Date(apmt.start).getHours()}:
                        {new Date(apmt.start).getMinutes().toString().length ===
                        1
                          ? "0" + new Date(apmt.start).getMinutes()
                          : new Date(apmt.start).getMinutes()}
                      </span>
                    </div>
                    <div className="d-flex">
                      <span>End Time</span>
                      <span>
                        {new Date(apmt.end).getHours()}:
                        {new Date(apmt.end).getMinutes().toString().length === 1
                          ? "0" + new Date(apmt.end).getMinutes()
                          : new Date(apmt.end).getMinutes()}
                      </span>
                    </div>
                  </div>
                )
              );
            })}
          </div>
          <span>
            {
              appointmentsList.filter((aptm) =>
                util.sameDay(new Date(aptm.start), new Date())
              ).length
            }{" "}
            Appointment/s
          </span>
        </div>
        <br />
      </div>
      <br />
      <div className="row">
        <div className="recent-activities col">
          <h1>Recent Activities</h1>
          {appointmentsList.map((apmt, i) => {
            return (
              apmt.status !== 0 && (
                <div
                  key={"apmt" + i}
                  className="d-flex flex-column recent-activity"
                >
                  <span>{apmt.title}</span>
                  <div className="d-flex">
                    <span>Start Time</span>
                    <span>
                      {new Date(apmt.start).getHours()}:
                      {new Date(apmt.start).getMinutes().toString().length === 1
                        ? "0" + new Date(apmt.start).getMinutes()
                        : new Date(apmt.start).getMinutes()}
                    </span>
                  </div>
                  <div className="d-flex">
                    <span>End Time</span>
                    <span>
                      {new Date(apmt.end).getHours()}:
                      {new Date(apmt.end).getMinutes().toString().length === 1
                        ? "0" + new Date(apmt.end).getMinutes()
                        : new Date(apmt.end).getMinutes()}
                    </span>
                  </div>
                  <div className="d-flex">
                    <span>Status: </span>
                    <span>{apmt.status === 1 ? "Done" : "Cancelled"}</span>
                  </div>
                </div>
              )
            );
          })}
        </div>
      </div>
    </Layout>
  );
};

export default DoctorDashboard;
