import "../css/doctors-advanced-search.css";
import avatar from "../avatar.jpg";
import { util } from "../public/util";
import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import Calendar from "../inputs/calendar";
import { Modal } from "react-bootstrap";

const DoctorsAdvancedSearch = ({ ...props }) => {
  let appointmentsList = [
    { title: "event 1", date: "2021-11-01", id: 1 },
    { title: "event 2", date: "2021-11-02" },
    { title: "event 2", date: "2021-11-02" },
  ];
  const [appointments, setAppointments] = useState(appointmentsList);
  const [showAppointmentModal, setShowAppointmentModal] = useState(false);
  const [startDate, setStartDate] = useState(new Date());
  const [datetime, setDatetime] = useState("");
  const [appointmentText, setAppointmentText] = useState("");
  const addAppointment = () => {
    let list = appointments;
    list.push({
      title: appointmentText,
      date: datetime.split("T")[0],
    });
    setAppointments(list);
    setShowAppointmentModal(false);
  };



  return (
    <div className="d-flex flex-column justify-content-center align-items-center">
      {props.data.map((doctorInfo, i) => {
        return (
          <div className="row col-9 mb-4" key={"doctor" + i}>
            <div className="col-2 d-flex justify-content-center align-items-center">
              <img src={avatar} className="doctor-profile" alt="" />
            </div>
            <div className="d-flex flex-column align-items-center justify-content-center col-2">
              <h1>{doctorInfo.name}</h1>
              <span></span>
              <h2>{doctorInfo.location.length} Location/s</h2>
              <h2>${doctorInfo.Price}</h2>
            </div>
            <div className="d-flex justify-content-between col-7 doctor-details">
              <div className="d-flex flex-column justify-content-center w-75 p-5 h-100">
                <Calendar
                  select={() => setShowAppointmentModal(true)}
                  events={appointments}
                  doctorId={doctorInfo.id}
                  addEvent = {showAppointmentModal}
                />
                <br />
                <button onClick={() => setShowAppointmentModal(true)}>
                  Add Appointment
                </button>
              </div>
              <div className="d-flex flex-column doctor-buttons">
                <Link to={"/view-profile/" + doctorInfo.id}>
                  <button>View Profile</button>
                </Link>
                <button>Add to favorite</button>
              </div>
            </div>
          </div>
        );
      })}
    </div>
  );
};

export default DoctorsAdvancedSearch;
