import React from "react";
import NavBar from "../../components/nav-bar";
import "../../css/doctor-dashboard.css";
import Calendar from "../../inputs/calendar";
import { util } from "../../public/util";

const DoctorDashboard = () => {
  let appointmentsList = [
    {
      title: "event 1",
      start: "2021-11-11T11:00:00",
      end: "2021-11-11T11:30:00",
      id: 1,
    },
    {
      title: "event 2",
      start: "2021-11-11T12:00:00",
      end: "2021-11-11T12:30:00",
      id: 1,
    },
    {
      title: "event 2",
      start: "2021-11-13T12:00:00",
      end: "2021-11-13T12:30:00",
      id: 1,
    },
    {
      title: "event 3",
      start: "2021-11-14T12:00:00",
      end: "2021-11-14T12:30:00",
      id: 1,
      backgroundColor: "#80ACED",
    },
  ];

  return (
    <div className="">
      <NavBar />
      <div className="container-fluid">
        <div className="row">
          <div className="side-menu col-2 d-flex flex-column">
            <div className="side-menu-item">
              <span>Home</span>
            </div>
            <div className="side-menu-item">
              <span>Messages</span>
            </div>
            <div className="side-menu-item">
              <span>Appointments History</span>
            </div>
            <div className="side-menu-item">
              <span>Invoices</span>
            </div>
            <div className="side-menu-item">
              <span>Manage Payment Methods</span>
            </div>
            <div className="side-menu-item">
              <span>Saved Products</span>
            </div>
          </div>
          <div className="col-10 d-flex ">
            <div className="col-5 d-flex justify-content-center">
              <Calendar
                allowEdit={false}
                events={appointmentsList}
                doctorId={1}
              />
            </div>
            <div className="col-5 appointments-number d-flex flex-column align-items-start  justify-content-between">
              <div>
                {appointmentsList.map((apmt, i) => {
                  return (
                    util.sameDay(new Date(apmt.start), new Date()) && (
                      <div key={"apmt"+i} className="d-flex flex-column">
                        <span>{apmt.title}</span>
                        <div className="d-flex">
                          <span>Start Time</span>
                          <span>
                            {new Date(apmt.start).getHours()}:
                            {new Date(apmt.start).getMinutes().toString()
                              .length === 1
                              ? "0" + new Date(apmt.start).getMinutes()
                              : new Date(apmt.start).getMinutes()}
                          </span>
                        </div>
                        <div className="d-flex">
                          <span>End Time</span>
                          <span>
                            {new Date(apmt.end).getHours()}:
                            {new Date(apmt.end).getMinutes().toString()
                              .length === 1
                              ? "0" + new Date(apmt.end).getMinutes()
                              : new Date(apmt.end).getMinutes()}
                          </span>
                        </div>
                      </div>
                    )
                  );
                })}
              </div>
              <span>
                {
                  appointmentsList.filter((aptm) =>
                    util.sameDay(new Date(aptm.start), new Date())
                  ).length
                }{" "}
                Appointment/s
              </span>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default DoctorDashboard;
