import "../css/doctors-advanced-search.css";
import avatar from "../avatar.jpg";
import { util } from "../public/util";
import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import Calendar from "../inputs/calendar";
import { Modal } from "react-bootstrap";
import { doctorController } from "../../controllers/doctor";

const DoctorsAdvancedSearch = ({ ...props }) => {
  const [appointmentsList, setAppointmentsList] = useState([]);
  const [scheduleList, setScheduleList] = useState([]);
  const [loading, isLoading] = useState(true);



  return (
    <div className="d-flex flex-column justify-content-center align-items-center">
      {props.data.map((doctorInfo, i) => {
          doctorController
          .getAllDoctorAppointments(5)
          .then((response) => {
            setAppointmentsList(response);
          })
          .then(() => {
            doctorController
              .getDoctorSchedule(5)
              .then((response) => {
                let list = response.data;
                for (let i = 0; i < list.length; i++) {
                  list[i]["is_schedule"] = true;
                }
                setScheduleList(list);
              })
              .then(() => {
                isLoading(false);
              });
          });
        return (
         !loading && <div className="row col-9 mb-4" key={"doctor" + i}>
            <div className="col-2 d-flex justify-content-center align-items-center">
              <img src={avatar} className="doctor-profile" alt="" />
            </div>
            <div className="d-flex flex-column align-items-center justify-content-center col-2">
              <h1>{doctorInfo.name}</h1>
              <span></span>
              <h2>{doctorInfo.location.length} Location/s</h2>
              <h2>${doctorInfo.Price}</h2>
            </div>
            <div className="d-flex justify-content-between col-8 doctor-details">
              <div className="d-flex flex-column justify-content-start w-100 h-100">
                <Calendar schedule={scheduleList} isDoctor={false} allowEdit={true} events={appointmentsList} doctorId={doctorInfo.id} />
                <br />
              </div>
              <div className="d-flex flex-column doctor-buttons w-25">
                <Link to={"/view-profile/" + doctorInfo.id}>
                  <button>View Profile</button>
                </Link>
                <button>Add to favorite</button>
              </div>
            </div>
          </div>
        );
      })}
    </div>
  );
};

export default DoctorsAdvancedSearch;
