import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { doctorController } from "../../controllers/doctor";
import Layout from "./layout";
import "../../css/schedule-settings.css";
import moment from "moment";

const ScheduleSettings = () => {
  const { id } = useParams();
  const [loading, setLoading] = useState(true);
  const [doctorData, setDoctorData] = useState({
    id: -1,
    name: "",
    country: "",
    profession_fk: -1,
    city: "",
    Price: "",
    location: [],
  });
  useEffect(() => {
    doctorController
      .getDoctorInfo(id)
      .then((response) => {
        setDoctorData(response.data);
      })
      .then(() => {
        setLoading(false);
      });
  }, []);

  const daysOfWeek = ["Mon", "Tue", "Wed", "Thu", "Fri", "Sat", "Sun"];

  const [timingArray, setTimingarray] = useState([
    {
      i: 0,
      start: "",
      end: "",
      type: "",
    },
  ]);
  const addTiming = () => {
    let temp = timingArray;
    temp = [...temp, { i: timingArray.length, start: "", end: "" }];
    setTimingarray(temp);
    console.log(temp);
  };
  const deleteTiming = (index) => {
    console.log(index);
    let temp = timingArray;

    temp = temp.filter((timing) => timing.i !== index);
    setTimingarray(temp);
  };

  const modifyStartTiming = (index, value) => {
    let temp = timingArray;
    temp[index].start = value;
    setTimingarray(temp);
  };
  const modifyEndTiming = (index, value) => {
    let temp = timingArray;
    temp[index].end = value;
    setTimingarray(temp);
  };
  const modifyAppointmentsType = (index, value) => {
    let temp = timingArray;
    temp[index].type = value;
    setTimingarray(temp);
  };

  const [loaction, setLocation] = useState("");
  const [daysSelected, setDaysSelected] = useState(
    Array.from({ length: 7 }, (_, i) => 0)
  );

  const updateDaysSelected = (index) => {
    let temp = daysSelected;
    temp[index] = temp[index] === 0 ? 1 : 0;
    setDaysSelected(temp);
  };

  let dt = new Date();
  let currentWeekDay = dt.getDay();
  let lessDays = currentWeekDay === 0 ? 6 : currentWeekDay - 1;
  let wkStart = new Date(new Date(dt).setDate(dt.getDate() - lessDays));
  let wkEnd = new Date(new Date(wkStart).setDate(wkStart.getDate() + 6));

  const getDayDate = (i) => {
    var curr = new Date(); // get current date
    var first = curr.getDate() - curr.getDay(); // First day is the day of the month - the day of the week
    var last = first + i + 1; // last day is the first day + 6

    return moment(new Date(curr.setDate(last))).format("YYYY-MM-DD");
  };

  const updateSchedule = () => {
    doctorController.getDoctorSchedule(id).then((response) => {
      for (let i = 0; i < timingArray.length; i++) {
        for (let j = 0; j < daysSelected.length; j++) {
          if (daysSelected[j] === 1) {
            doctorController.addDoctorAppointmentSchedule({
              id: response.length + i + 1,
              doctor_fk: id,
              start: getDayDate(j) + "T" + timingArray[i].start,
              end: getDayDate(j) + "T" + timingArray[i].end,
              loaction: loaction,
              type: timingArray[i].type,
            });
          }
        }
      }
    });
  };

  return (
    <Layout>
      {!loading && (
        <div className="d-flex justify-content-center">
          <div className="col-8 schedule-div">
            <h3>
              Weekly Schedule (From {wkStart.toString().substring(0, 15)} into{" "}
              {wkEnd.toString().substring(0, 15)})
            </h3>
            <br />
            <div className="d-flex flex-column">
              <span>Location</span>
              <select onChange={(e) => setLocation(e.target.value)}>
                <option selected disabled>
                  Select your location
                </option>
                {doctorData.location.map((l) => {
                  return <option>{l}</option>;
                })}
              </select>
            </div>
            <br />
            <div className="d-flex flex-column">
              <span>Add your timings</span>
              <input
                type="button"
                className="input"
                value="Add Timing"
                onClick={() => addTiming()}
              />
              <div className="d-flex w-100 flex-column justify-content-center">
                {timingArray.map((time, index) => {
                  return (
                    <div className="d-flex  justify-content-between">
                      <input
                        className="input"
                        placeholder="Start Time"
                        type="time"
                        onChange={(e) =>
                          modifyStartTiming(index, e.target.value)
                        }
                      />
                      <input
                        className="input"
                        placeholder="End Time"
                        type="time"
                        onChange={(e) => modifyEndTiming(index, e.target.value)}
                      />
                      <select
                        onChange={(e) =>
                          modifyAppointmentsType(index, e.target.value)
                        }
                        className="input"
                      >
                        <option selected disabled>
                          Select type
                        </option>
                        <option value={0}>Physical</option>
                        <option value={1}>Virtual</option>
                        <option value={2}>Physical {"&"} Virtual</option>
                      </select>
                      <input
                        type="button"
                        value={"X"}
                        className="input close"
                        onClick={() => deleteTiming(index)}
                      />
                    </div>
                  );
                })}
              </div>
              <br />
              <div className="d-flex flex-column">
                <label>Select your preffered days</label>
                <div className="d-flex  align-items-center justify-content-center flex-wrap">
                  {daysOfWeek.map((day, i) => {
                    return (
                      <div
                        key={"day" + i}
                        className="d-flex input align-items-center justify-content-center mx-4"
                      >
                        <input
                          onChange={() => updateDaysSelected(i)}
                          type="checkbox"
                          value={day}
                        />
                        <label>{day}</label>
                      </div>
                    );
                  })}
                </div>
              </div>
              <br />
              <div className="d-flex flex-column">
                <input
                  type="button"
                  value={"Add Appointment Schedule"}
                  className="input"
                  onClick={() => updateSchedule()}
                />
              </div>
            </div>
          </div>
        </div>
      )}
    </Layout>
  );
};

export default ScheduleSettings;
