document.addEventListener("DOMContentLoaded", function (event) {
  var x = window.matchMedia("(max-width: 980px)");
  if (x.matches) {
    // If media query matches
    document.getElementById("side-menu").classList.add("collapse");
  } else {
    document.getElementById("side-menu").classList.remove("collapse");
  }

  document.getElementById("side-menu-sm").onclick = function () {
    var x = document.getElementById("side-menu");
    if (x.style.display === "none") {
      x.style.display = "block";
    } else {
      x.style.display = "none";
    }
  };
});
