import "../css/doctor-appointments.css";
import avatar from "../avatar.jpg";

import { util } from "../public/util";
import CustomPagination from "../inputs/custom-pagination";
import { useEffect, useState } from "react";

const DoctorRecentActivities = ({...props}) => {
    const [data, setData] = useState([]);
  useEffect(() => {
      setData(props.appointments.filter((item) => item.status !== 0))
  }, [props.appointments])
  return (
    <CustomPagination data={data}>
      {data.map((apmt, i) => {
        return (
            <div className="appointment" key={"apmt" + i}>
              <div className="d-flex justify-content-between">
                <div>
                  <img src={avatar} className="patient-profile-img" alt="" />
                  <span className="px-3">patient</span>
                </div>
                <span>{apmt.title}</span>
              </div>
              <div className="content">
                <div className="d-flex">
                  <span>Start Time:</span>
                  <span>
                    {new Date(apmt.start).getHours()}:
                    {new Date(apmt.start).getMinutes().toString().length === 1
                      ? "0" + new Date(apmt.start).getMinutes()
                      : new Date(apmt.start).getMinutes()}
                  </span>
                </div>
                <div className="d-flex">
                  <span>End Time:</span>
                  <span>
                    {new Date(apmt.end).getHours()}:
                    {new Date(apmt.end).getMinutes().toString().length === 1
                      ? "0" + new Date(apmt.end).getMinutes()
                      : new Date(apmt.end).getMinutes()}
                  </span>
                </div>
              </div>
            </div>

        );
      })}
    </CustomPagination>
  );
}

export default DoctorRecentActivities;
