import { useEffect, useRef, useState } from "react";
import "../css/price-range.css";

const PriceRange = () => {
  const sliderRef = useRef();
  const startRef = useRef();

  const [pressed, setPressed] = useState(false);

  const setPositions = (e) => {
    const x = e.movementX;
    if (pressed) {
      startRef.current.style.transform = "translate(" + x + "px)";
    }
  };

  return (
    <div
      ref={sliderRef}
      onMouseMove={(e) => setPositions(e)}
      className="slider-range d-flex"
    >
      <div
        disabled
        ref={startRef}
        onMouseDown={(e) => setPressed(true)}
        onMouseUp={() => setPressed(false)}
        className="start-range"
      ></div>
      <div className="end-range"></div>
    </div>
  );
};

export default PriceRange;
