import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { productsController } from "../../controllers/product-page";

const Product = () => {
  let { productId } = useParams().id;

  console.log(productId)

  const [productDetails, setProductDetails] = useState({});
  useEffect(() => {
    productsController.getProduct(productId).then((response) => {
      setProductDetails(response.data[0]);
    });
  });
  return (
      <div className="container">
          <div className="header d-flex justify-content-between">
            <div className="col-4">
                {productDetails.name} by {productDetails.company}
            </div>
            <div className="col-4">
               <button className="buy-now">Buy Now for {productDetails.price}</button>
            </div>
          </div>
      </div>
  )
};

export default Product;
