import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { doctorController } from "../../controllers/doctor";
import Layout from "./layout";

const ScheduleSettings = () => {
  const { id } = useParams();
  const [doctorData, setDoctorData] = useState({});
  useEffect(() => {
    doctorController.getDoctorInfo(id).then((response) => {
      console.log(response.data[0]);
      setDoctorData(response.data[0]);
    });
  }, []);

  const [times, setTimes] = useState([
    {
      start: "",
      end: "",
    },
  ]);
  const addTiming = () => {
    let temp = times;
    temp = [...temp, { start: "", end: "" }];
    setTimes(temp);
  };

  return (
    <Layout>
      <div className="d-flex flex-column">
        <select>
          {doctorData.location.map((location) => {
            return <option>{location.location}</option>;
          })}
        </select>
        <div>
          <input
            type="button"
            className="input"
            value="Add Timing"
            onClick={() => addTiming()}
          />
          {times.map((time) => {
            return (
              <div className="d-flex justify-content-between">
                <input className="input" placeholder="Start Time" type="time" />
                <input className="input" placeholder="End Time" type="time" />
              </div>
            );
          })}
        </div>
      </div>
    </Layout>
  );
};

export default ScheduleSettings;
