import axios from "axios";
import Geocode from "react-geocode";
import configData from "../configData.json";

const SERVER_URL = "localhost";
const SERVER_PORT = "5000";

const apiURL = "http://" + SERVER_URL + ":" + SERVER_PORT;

const getProducts = () => {
  return axios.get(apiURL + "/products");
};

const getProduct = (productId) => {
  return axios.get(apiURL + "/products?id=" + productId);
};

const getProductsCategories = async () => {
  return await axios
    .get(apiURL + "/categories?name=Products")
    .then((response) => {
      return axios.get(
        apiURL + "/profession?category_fk=" + response.data[0].category_fk
      );
    });
};

export const productsController = { getProducts, getProduct,getProductsCategories };
